{"link": "https://www.eclipse.org/paho/clients/c/embedded/", "text": "(ipstack);\n\n    const char* hostname = \"iot.eclipse.org\";\n    int port = 1883;\n    printf(\"Connecting to %s:%d\\n\", hostname, port);\n    int rc = ipstack.connect(hostname, port);\n\tif (rc != 0)\n\t    printf(\"rc from TCP connect is %d\\n\", rc);\n\n\tprintf(\"MQTT connecting\\n\");\n    MQTTPacket_connectData data = MQTTPacket_connectData_initializer;\n    data.MQTTVersion = 3;\n    data.clientID.cstring = (char*)\"mbed-icraggs\";\n    rc = client.connect(data);\n\tif (rc != 0)\n\t    printf(\"rc from MQTT connect is %d\\n\", rc);\n\tprintf(\"MQTT connected\\n\");\n\n    rc = client.subscribe(topic, MQTT::QOS2, messageArrived);\n    if (rc != 0)\n        printf(\"rc from MQTT subscribe is %d\\n\", rc);\n\n    MQTT::Message message;\n\n    // QoS 0\n    char buf[100];\n    sprintf(buf, \"Hello World!  QoS 0 message from app version %f\", version);\n    message.qos = MQTT::QOS0;\n    message.retained = false;\n    message.dup = false;\n    message.payload = (void*)buf;\n    message.payloadlen = strlen(buf)+1;\n    rc = client.publish(topic, message);\n\tif (rc != 0)\n\t\tprintf(\"Error %d from sending QoS 0 message\\n\", rc);\n    else while (arrivedcount == 0)\n        client.yield(100);\n\n    rc = client.unsubscribe(topic);\n    if (rc != 0)\n        printf(\"rc from unsubscribe was %d\\n\", rc);\n\n    rc = client.disconnect();\n    if (rc != 0)\n        printf(\"rc from disconnect was %d\\n\", rc);\n\n    ipstack.disconnect();\n\n    return 0;\n}"}
